import React, { useState } from "react";
import axios from "axios";
import WeatherCard from "./WeatherCard";
import WeatherGraph from "./WeatherGraph";
import SearchBar from "./SearchBar";


function App() {
  const [cities, setCities] = useState([]);
    const [selectedCity, setSelectedCity] = useState(null);

    const fetchWeatherData = async (city) => {
        try {
            const response = await axios.get(`http://localhost:5000/api/weather/${city}`);
            return response.data;
        } catch (error) {
            console.error("Error fetching weather data:", error);
            alert("City not found or API error.");
            return null;
        }
    };

    const addCity = async (city) => {
        if (cities.find((c) => c.city === city)) {
            alert("City already added.");
            return;
        }
        const data = await fetchWeatherData(city);
        if (data) setCities([...cities, data]);
    };

    const removeCity = (city) => {
        setCities(cities.filter((c) => c.city !== city));
    };
  return (
    <div className="App">
      <div>
            <h1>Weather Forecast Dashboard</h1>
            <SearchBar onSearch={addCity} />
            <div className="weather-cards">
                {cities.map((cityData) => (
                    <WeatherCard
                        key={cityData.city}
                        data={cityData}
                        onRemove={removeCity}
                        onSelect={() => setSelectedCity(cityData)}
                    />
                ))}
            </div>
            {selectedCity && <WeatherGraph data={selectedCity} />}
        </div>
    </div>
  );
}

export default App;
