import React, { useState } from 'react'

const SearchBar = () => {
    const [input, setInput] = useState("");

    const handleSearch = () => {
        if (input.trim()) {
            onSearch(input.trim());
            setInput("");
        }
    };
  return (
    <div>
      <div>
            <input
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="Enter city name"
            />
            <button onClick={handleSearch}>Add City</button>
        </div>
    </div>
  )
}

export default SearchBar
