import React, { useState } from "react";
import axios from "axios";

const WeatherForecast = () => {
  const [city, setCity] = useState("");
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState("");

  const fetchWeatherData = async (cityName) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/weather/${cityName}`);
      setWeatherData(response.data);
      setError(""); // Clear any previous errors
    } catch (err) {
      console.error("Error fetching weather data:", err);
      setError("City not found or API error. Please try again.");
      setWeatherData(null); // Clear previous data
    }
  };

  const handleSearch = () => {
    if (city.trim()) {
      fetchWeatherData(city.trim());
      setCity(""); // Clear input after search
    }
  };

  return (
    <div className="container">
      <h1 className="text-center my-4">Weather Forecast</h1>
      <div className="d-flex mb-4">
        <input
          type="text"
          className="form-control"
          placeholder="Enter city name"
          value={city}
          onChange={(e) => setCity(e.target.value)}
        />
        <button className="btn btn-primary ml-2" onClick={handleSearch}>
          Search
        </button>
      </div>

      {error && <div className="alert alert-danger">{error}</div>}

      {weatherData && (
        <div className="weather-details">
          <h2>{weatherData.city}</h2>
          <p>
            <strong>Today's High:</strong> {weatherData.highTemp}°C
          </p>
          <p>
            <strong>Today's Low:</strong> {weatherData.lowTemp}°C
          </p>
          <h3>3-Day Forecast</h3>
          <ul>
            {weatherData.forecast.map((day, index) => (
              <li key={index}>
                <strong>{day.day}:</strong> {day.temp}°C, {day.condition}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default WeatherForecast;
