import axios from "axios";
import WeatherCard from "./WeatherCard";
import WeatherGraph from "./WeatherGraph";
import SearchBar from "./SearchBar";


function App() {

  return (
    <div className="App">
      <div>
            <h1>Weather Forecast Dashboard</h1>
            <SearchBar onSearch={addCity} />
            <div className="weather-cards">
                {cities.map((cityData) => (
                    <WeatherCard
                        key={cityData.city}
                        data={cityData}
                        onRemove={removeCity}
                        onSelect={() => setSelectedCity(cityData)}
                    />
                ))}
            </div>
            {selectedCity && <WeatherGraph data={selectedCity} />}
        </div>
    </div>
  );
}

export default App;
